/* Dashboard.module.scss */

.sidebar {
    // position: fixed;
    top: 0;
    left: 0;
    width: 20%;
    // height: 100%;
    background-color: #2c3e50;
    color: #ecf0f1;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 20px;
    box-shadow: 2px 0 5px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
  
    @media (max-width: 768px) {
      width: 40%;
    }
  
    @media (max-width: 480px) {
      width: 60%;
    }
  }
  
  .topSection {
    // margin-bottom: 40px;
    h2 {
      font-size: 24px;
      font-weight: bold;
      color: #ecf0f1;
    }
  }
  .hover{
    color: white;
    text-decoration: none;
  }
  .hover:hover{color: black;} 
  .menu {
    list-style: none;
    padding: 0;
    width: 100%;
  
    li {
      display: flex;
      align-items: center;
      padding: 15px 10px;
      margin: 5px 0;
      font-size: 18px;
      cursor: pointer;
      transition: all 0.3s ease;
      border-radius: 4px;
  
      &:hover {
        background-color: white;
        color: black;
      }
      
  
      svg {
        margin-right: 10px;
      }
    }
    
  }
