commits.content:
.container {
    background: #2f87ed;
  }
  .cardContainer {
    display: flex;
    gap: 0%;
    @media screen and (max-width: 768px) {
      flex-direction: column;
    }
  }
  .bg {
    background-image: linear-gradient(to right, #6a11cb 0%, #2575fc 100%);
    margin: 0 auto;
    margin-top: 20px;
    width: 90%;
    align-items: center;
    box-shadow: rgba(0, 0, 0, 0.56) 0px 22px 70px 4px;
    align-content: center;
    border: 6px solid black;
    font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
    border-radius: 15px;
    h1 {
      color: #ffc107;
      align-items: center;
      text-align: center;
      padding-top: 2%;
      &:hover{
        transform: translateY(-10px);
        transition:transform 0.5s linear;
        cursor: pointer;
      }
    }
    p {
      color: white;
      text-align: center;
      padding-top: 0%;
      font-weight: bolder;
      font-size: larger;
    }
    height: auto;
  }
  .card {
    display: flex;
    flex-direction: column;
    width: 24%;
    height: 50vh;
    //   border: 4px solid black;
    position: relative;
    //   padding-left: ;
    margin-left: 0.8%;
    margin-bottom: 2%;
    animation: wave 2s ease-in-out forwards ;
    &:hover{
      transform: scale(1.2);
      cursor: pointer;
    }
    @keyframes wave {
      0%{opacity: 0;transform: translateX(-100px);}
      25%{opacity: 1;transform: translateX(-70px);transform: translateY(-30px);}
      75%{opacity: 1;transform: translateX(-40px);transform: translateY(0px);}
      100%{opacity: 1;transform: translate(0px,0px);}
    }
    @media screen and (max-width: 768px) {
      margin-left: 0%;
      height: 43vh;
      width: 100%;
    }
    img {
      align-items: center;
      align-content: center;
      margin: 0 auto;
      // height: auto;width: auto;
      width: 100%;
      position: absolute;
      @media screen and (max-width: 768px) {
        margin-left: 0%;
        width: 100%;
        // position: relative;
      }
    }
    h1 {
      color: white;
      position: relative;
      padding-top: 0%;
      font-size: 26px;
      // margin: 0px;
      top: 70%;
      @media screen and (max-width: 768px) {
        font-size: 34px;
        top: 60%;
        color: white;
      }
    }
  }
  .links{height: 50vh;
  text-decoration: none;
  &:hover{
    transform: scale(1.07);
    transition: transform 0.5s ease-in-out;
  }
  }
