.container {
    position: relative;  // Ensure the container positions its children relative to it
    width: 100vw;
    height: 100vh;
    overflow: hidden;    // Hide any overflow content
    display: flex;
    justify-content: center;
    flex-direction: column;

    video {
        position: absolute;  // Position video absolutely within the container
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        object-fit: cover;  // Ensure video covers the container
        z-index: -1;       // Place video behind other elements
        filter:blur(3.2px);
        filter:contrast(1.3);
    }
    .header{
        width: 100%;
        color: white;
        font-family: "Roboto", sans-serif;
        font-weight: 700;
        font-style: normal;
        font-size: 20px;
    }
    .boxes{
        width: 100%;
        display: flex;
        flex-direction: row;
        justify-content: center;
        align-items: center;

       
        .box {
            height: 200px;
            width: 200px;
            border: 1px solid white;
            color: white;
            border-radius: 10px;
            background-color: rgba(0, 0, 0, 0.348);
            cursor: pointer;
            animation: rotate 1s linear forwards, fall 3s ease-out forwards;
            z-index: 1;
            display: flex;
            justify-content: center;
            align-items: center;
            font-size: 50px;
            position: relative;
            margin: 20px;
            box-shadow: rgba(255, 255, 255, 0.9) 0px 19px 38px, rgba(255, 255, 255, 0.22) 0px 15px 12px;
            overflow: hidden;
            transition: transform 0.3s ease; 
            img {
                height: 200px;
                width: 200px;
                object-fit:cover;
                width: 100%;
                position: absolute;
                top: 0px;
                left: 0px;
            }
        
            p {
                font-size: 20px;
            }
        }
    }
   
    

    @keyframes rotate {
        0% {
            transform: rotate(0deg);
        }
        100% {
            transform: rotate(360deg);
        }
    }

    @keyframes fall {
        0% {
            top: -100%;
            opacity: 0;
        }
        100% {
            top: 10%;
            opacity: 1;
        }
    }
}

@media screen and (max-width: 768px) {
    .container{
        display: flex;
        flex-direction: column;
        .boxes{
            flex-direction: column;
            justify-content: center;
            align-items: center;
            position:absolute;
            .box {
                height: 100px;
                width: 200px;
                position: relative;  // Ensure boxes are positioned relative to the container
                margin: 20px;  // Add margin to space out the boxes
                top: 10px;
                box-shadow: rgba(255, 255, 255, 0.3) 0px 19px 38px, rgba(255, 255, 255, 0.22) 0px 15px 12px;
                img{
                    object-fit:cover;
                    position:relative;
                    height: 100px;
                }
            }
        }
    }
    
}
